mapper.select:

select 

,

from dbo.InternalMessenger
where MessageID = #{messageid,jdbcType=VARCHAR}
  and MessageDateTime = #{messagedatetime,jdbcType=TIMESTAMP}

mapper.delete:

delete from dbo.InternalMessenger
where MessageID = #{messageid,jdbcType=VARCHAR}
  and MessageDateTime = #{messagedatetime,jdbcType=TIMESTAMP}

mapper.update:

update dbo.InternalMessenger
set ConnectionID = #{connectionid,jdbcType=VARCHAR},
  UserCode = #{usercode,jdbcType=INTEGER},
  RecipientUserCode = #{recipientusercode,jdbcType=INTEGER},
  DeliveryStatus = #{deliverystatus,jdbcType=VARCHAR},
  DeleteStat = #{deletestat,jdbcType=VARCHAR}
where MessageID = #{messageid,jdbcType=VARCHAR}
  and MessageDateTime = #{messagedatetime,jdbcType=TIMESTAMP}

mapper.select:
		select * from dbo.InternalMessenger
		where ConnectionID =
		#{connectionID,jdbcType=VARCHAR}
		and DeleteStat != 'B'
		and DeleteStat != #{usercode,jdbcType=VARCHAR} 
		order by MessageDateTime

		select TOP 1 * from dbo.InternalMessenger
		where ConnectionID
		= #{connectionID,jdbcType=VARCHAR}
		and DeleteStat != 'B'
		and DeleteStat != #{usercode,jdbcType=VARCHAR}
		order by MessageDateTime desc;

